import { prefix } from '../../globals';
import classnames from 'classnames';

let keysDown = [];

function PanelSwitcher ({ children, aside, activePanel = 'left' }) {

  return (
    <div className={classnames(`${prefix}-panel-switcher`, `${prefix}-panel-switcher--${activePanel}`)} onKeyDown={trapFocus} onKeyUp={clearKeysDown}>
      <div className={`${prefix}-panel-switcher__panel`}>
        {children}
      </div>
      <div className={`${prefix}-panel-switcher__panel`}>
        {aside}
      </div>
    </div>
  );
}

function clearKeysDown (e) {
  // remove just the key that was removed
  console.log(e)
  keysDown = [];
}

function trapFocus (e) {
  console.log(e);
  const isTabPressed = e.key === 'Tab' || e.keyCode === 9;
  const isShiftPressed = e.key === 'Shift' || e.keyCode === 16;
  const current = e.target;
  let scope = e.target;

  if (!isTabPressed) {
    return;
  }

  if (isShiftPressed || isTabPressed) {
    keysDown.push(e.keyCode);
  }

  while (!scope.classList.contains(`${prefix}-panel-switcher__panel`)) {
    scope = scope.parentNode;
  }

  const focusable = scope.querySelectorAll('input, button');
  const first = focusable[0];
  const last = focusable[focusable.length - 1];

  if (current === last) {
    e.preventDefault();

    console.log('focus on', first);
    first.focus();
  }
}

export default PanelSwitcher;
