import { useEffect, useState } from 'react';
import { prefix } from '../../globals';
import getUser from '../../utilities/getUser';
import Button from '../Button';
import TextInput from '../TextInput';

function EditForm ({ userId, switchPanel }) {
  const [user, setUser] = useState({});

  useEffect(() => {
    if (userId) {
      getUser(userId).then(d => setUser(d));
    } else {
      setUser({});
    }
  }, [userId]);

  return (
    <form className={`${prefix}-user-form`}>
      <TextInput
        className={`${prefix}-user-form__input`}
        id="name"
        label="Name"
        value={user.name || ''} />
      <TextInput
        className={`${prefix}-user-form__input`}
        id="email"
        label="Email"
        value={user.email || ''} />
      <TextInput
        className={`${prefix}-user-form__input`}
        id="phone"
        label="Phone"
        value={user.phone || ''} />
      <TextInput
        className={`${prefix}-user-form__input`}
        id="id"
        value={user.id || ''}
        type="hidden" />
      <div className={`${prefix}-user-form__actions`}>
        <Button size="lg" type="primary">Save</Button>
        <Button size="lg" type="secondary" onClick={switchPanel}>Cancel</Button>
      </div>
    </form>
  );

  function formatPhone () {

  }
}

export default EditForm;
