import { useEffect, useState } from 'react';
import { prefix } from '../../globals';
import getUser from '../../utilities/getUser';
import Button from '../Button';
import TextInput from '../TextInput';

function EditForm ({ userId, switchPanel }) {
  const [user, setUser] = useState({});

  // SUCCESS MESSAGE?

  useEffect(() => {
    if (userId) {
      getUser(userId).then(d => setUser(d));
    } else {
      setUser({});
    }
  }, [userId]);

  console.log(user, user.name)

  return (
    <form className={`${prefix}-edit-form`}>
      <TextInput className={`${prefix}-edit-form__input`} id="name" label="name" value={user.name || ''} />
      <TextInput className={`${prefix}-edit-form__input`} id="email" label="email" value={user.email || ''} />
      <TextInput className={`${prefix}-edit-form__input`} id="phone" label="phone" value={user.phone || ''} />
      <TextInput className={`${prefix}-edit-form__input`} id="id" value={user.id || ''} type="hidden" />
      <Button type="primary">Save</Button>
      <Button type="secondary" onClick={switchPanel}>Cancel</Button>
    </form>
  );
}

export default EditForm;
