import { useEffect, useState } from 'react';
import { prefix } from '../../globals';
import getUser from '../../utilities/getUser';
import saveUser from '../../utilities/saveUser';
import validate from '../../utilities/validation';
import Button from '../Button';
import TextInput from '../TextInput';

class User {
  constructor(user = {}, validate) {
    this.name = validate ? user.name : user.name || '';
    this.email = validate ? user.email : user.email || '';
    this.phone = validate ? user.phone : user.phone || '';

    if (user.id) {
      this.id = user.id;
    }
  }
}

function UserForm ({ editUser, sharedState, switchPanel }) {
  const [user, setUser] = useState(new User());
  const [validationResults, setValidationResults] = useState(new User({}, true));

  useEffect(() => {
    if (editUser.id) {
      getUser(editUser.id).then(d => setUser(new User(d)));
    } else {
      setUser(new User());
    }
  }, [editUser]);

  return (
    <form className={`${prefix}-user-form`} onSubmit={e => saveForm(e)}>
      <TextInput
        className={`${prefix}-user-form__input`}
        id="name"
        label="Name"
        validate={getValidation(validationResults.name)}
        onChange={e => trackUserChanges(e, 'name')}
        value={user.name || ''} />
      <TextInput
        className={`${prefix}-user-form__input`}
        id="email"
        label="Email"
        validate={getValidation(validationResults.email)}
        onChange={e => trackUserChanges(e, 'email')}
        value={user.email || ''} />
      <TextInput
        className={`${prefix}-user-form__input`}
        id="phone"
        label="Phone"
        validate={getValidation(validationResults.phone)}
        onKeyDown={e => {
          e.target.value = displayPhone(e.target.value);
        }}
        onKeyUp={e => trackValidation(e, 'phone')}
        onBlur={e => trackUserChanges(e, 'phone')}
        value={displayPhone(user.phone) || ''} />
      <input
        className={`${prefix}-user-form__input`}
        id="id"
        value={user.id || ''}
        type="hidden" />
      <div className={`${prefix}-user-form__actions`}>
        <Button size="lg" styleType="primary" type="submit">Save</Button>
        <Button size="lg" styleType="secondary" onClick={clearForm}>Cancel</Button>
      </div>
    </form>
  );

  function trackUserChanges (e, prop) {
    user[prop] = e.target.value.trim();
    setUser(new User(user));
  }

  function trackValidation (e, prop) {
    validationResults[prop] = validate[prop](e.target.value.trim());
    setValidationResults(new User(validationResults, true));
  }

  function clearForm (e) {
    switchPanel(e);
    setUser(new User());
  }

  function saveForm (e) {
    e.preventDefault();
    saveUser(user).then((savedUser) => {
      switchPanel(e);
      sharedState.saveUser(savedUser, editUser.index);
    });
  }

  function displayPhone (str = '') {
    let formattedPhone;
    str = str.replace(/\D/g, '');

    if (str.length <= 7) {
      formattedPhone = [str.slice(0,3), str.slice(3,7)];
    } else {
      formattedPhone = [str.slice(0,3), str.slice(3,6), str.slice(6,10)];
    }

    return formattedPhone.filter(d => d.length).join('-');
  }

  function getValidation (result) {
    if (result === false) {
      return 'error';
    } else if (result === true) {
      return 'valid';
    }

    return '';
  }
}

export default UserForm;
