import { prefix } from '../../globals';
import classnames from 'classnames';

function PanelSwitcher ({ children, aside, activePanel = 'left' }) {

  return (
    <div className={classnames(`${prefix}-panel-switcher`, `${prefix}-panel-switcher--${activePanel}`)} onKeyDown={trapFocus}>
      <div className={`${prefix}-panel-switcher__panel`}>
        {children}
      </div>
      <div className={`${prefix}-panel-switcher__panel`}>
        {aside}
      </div>
    </div>
  );

  function trapFocus (e) {
    console.log(e);
    const currentElement = e.target;

    while (!currentElement.classList.has(`${prefix}-panel-switcher__panel`)) {
      currentElement = currentElement.parentNode;
    }

    const focusableElements = currentElement.querySelectorAll('input, button');
    const firstElement = focusableElements[0];
    const lastElement = focusableElements[focusableElements.length - 1]

    console.log(focusableElements, firstElement, lastElement, e.target);
  }
}

export default PanelSwitcher;
