import { useEffect, useState } from 'react';
import deleteUser from '../../utilities/deleteUser';
import { prefix } from '../../globals';
import classnames from 'classnames';
import Button from '../Button';

function User ({ id, name, email, phone, deleteUserFromState }) {
  const [status, setStatus] = useState('');

  // TODO: build in on entrance, or just first load?
  // TODO: Delete animate, and call to server
  // TODO: Edit, swipe right to edit form

  return (
    <li className={classnames(`${prefix}-user-item`, status)}>
      <div className={`${prefix}-user-item__brief`}>
        <p className={`${prefix}-user-item__primary`}>{name}</p>
        <p className={`${prefix}-user-item__secondary`}>{email || 'n/a'}</p>
      </div>
      <aside className={`${prefix}-user-item__actions`}>
        <Button
          type="primary"
        >
          edit
        </Button>
        <Button
          type="primary"
          onClick={() => deleteItem(id)}
        >
          delete
        </Button>
      </aside>
    </li>
  );

  function deleteItem (id) {
    deleteUser(id).then(() => {
      setStatus(`${prefix}-user-item--deleted`);

      setTimeout(() => {
        deleteUserFromState();
      }, 300);
    }).catch((error) => {
      console.error('Error:', error);
    });
  }
}

export default User;
