// This validation is more to improve the user's experience.
// The backend should handle the brunt of the validation.
export default {
  email: (str) => {
    return /[\@]/g.test(str) && /[\.]/g.test(str);
  },
  name: (str) => {
    if (str.length) {
      return true;
    }

    return false;
  },
  phone: (str) => {
    str = str.replace(/\D/g, '');

    if (str.length === 10 || str.length === 7) {
      return true;
    }

    return false;
  }
}
