import { useEffect, useState } from 'react';
import { prefix } from './globals';
import classnames from 'classnames';
import getUsers from '../utilities/getUsers';
import Popup from './components/Popup';
import UserList from './components/UserList';
import PanelSwitcher from './components/PanelSwitcher';
import Header from './components/Header';
import UserForm from './components/UserForm';

function App() {
  const [autoOpen, setAutoOpen] = useState(false);
  const [panel, setPanel] = useState(false);
  const [activeUserId, setActiveUserId] = useState('');
  const [users, setUsers] = useState([]);

  useEffect(() => {
    setTimeout(() => {
      setAutoOpen(true);
    }, 1500)
  }, []);

  useEffect(() => {
    getUsers().then(d => setSortUsers(d));
  }, []);

  return (
    <main className="lw-main">
      <Popup open={autoOpen}>
        <PanelSwitcher
          aside={
            <>
              <Header title={activeUserId ? 'Edit user' : 'Create user'} active={autoOpen} />
              <UserForm
                userId={activeUserId}
                switchPanel={e => switchPanel('left', e)}
              />
            </>
          }
          activePanel={panel}>
          <Header title="User list" active={autoOpen} />
          <UserList
            switchPanel={e => switchPanel('right', e)}
          />
        </PanelSwitcher>
      </Popup>
    </main>
  );

  function switchPanel (panelName, e) {
    e.preventDefault();
    setPanel(panelName);
    setActiveUserId(e.target.dataset.userid);
    return panel;
  }

  function addUserToState (user) {
    users.push(user);
    setSortUsers([...users]);
  }

  function updateUserInState (index, user) {
    users.splice(index, 1, user);
    setSortUsers([...users]);
  }

  function deleteUserFromState (index) {
    users.splice(index, 1);
    setSortUsers([...users]);
  }
}




export default App;
